mapper.select:
select

from sh_chat_conversation
where user_a_id = #{userAId,jdbcType=BIGINT}
  and user_b_id = #{userBId,jdbcType=BIGINT}
  and idle_id = #{idleId,jdbcType=BIGINT}

select

from sh_chat_conversation
where user_a_id = #{userId,jdbcType=BIGINT}
   or user_b_id = #{userId,jdbcType=BIGINT}
order by update_time desc, id desc

mapper.insert:
insert into sh_chat_conversation (user_a_id, user_b_id, idle_id,
  context_snapshot, last_message_preview, last_message_time,
  user_a_unread, user_b_unread, create_time, update_time
  )
values (#{userAId,jdbcType=BIGINT}, #{userBId,jdbcType=BIGINT}, #{idleId,jdbcType=BIGINT},
  #{contextSnapshot,jdbcType=LONGVARCHAR}, #{lastMessagePreview,jdbcType=VARCHAR}, #{lastMessageTime,jdbcType=TIMESTAMP},
  #{userAUnread,jdbcType=INTEGER}, #{userBUnread,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
