mapper.select:
select

from sh_chat_message
where conversation_id = #{conversationId,jdbcType=BIGINT}

  and id < #{anchorId,jdbcType=BIGINT}

order by id desc
limit #{pageSize,jdbcType=INTEGER}

mapper.insert:
insert into sh_chat_message (conversation_id, sender_id, receiver_id,
  idle_id, message_type, content,
  extra_payload, send_time, read_time
  )
values (#{conversationId,jdbcType=BIGINT}, #{senderId,jdbcType=BIGINT}, #{receiverId,jdbcType=BIGINT},
  #{idleId,jdbcType=BIGINT}, #{messageType,jdbcType=INTEGER}, #{content,jdbcType=LONGVARCHAR},
  #{extraPayload,jdbcType=LONGVARCHAR}, #{sendTime,jdbcType=TIMESTAMP}, #{readTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update sh_chat_message
set read_time = #{readTime,jdbcType=TIMESTAMP}
where conversation_id = #{conversationId,jdbcType=BIGINT}
  and receiver_id = #{receiverId,jdbcType=BIGINT}
  and read_time is null
